  /**
   * リスナーの「視聴解約」
   */
  public function remove($observer)
  {
    foreach ($this->observers as $obs) {
      if ($observer != $obs) {
        $this->observers[] = $obs; //observerを上書きしてるのか？これ動くの？
      }
    }
  }

  public function notify($e)
  {
    foreach ($this->observers as $obs) {
      $obs->receive($e);
    }
