class ConfidantFactory
{
  private $confidant = array();
  private static $instance = null;

/**
*private なコンストラクタ
*/
  private function __construct()
  {
  }

/**
* 唯一のインスタンス取得メソッド
*/
  public static function getInstance()
  {
    if (!self::$instance) {
      self::$instance = new ConfidantFactory();
    }
    return self::$instance;
  }

  /**
