    if (!$this->confidant[$key]) {
      $this->confidant[$key] = new Confidant($key);
      print "[$key]を新たに雇うのです。";
    } else {
      print "すでにいるので使いまわしましょう。";
    }
    return $this->confidant[$key];
  }
}

