public class Directory extends Entry {
    private String name;                    // �ǥ��쥯�ȥ��̾��
    private ArrayList directory = new ArrayList();      // �ǥ��쥯�ȥꥨ��ȥ�ν���
    public Directory(String name) {         // ���󥹥ȥ饯��
