// childのプロパティがシャローコピーされてしまった例
say($originalObj->child->xyzzy == "vim" ? "OK" : "NG"); // NG
say($cloneObj->child->xyzzy == "emacs" ? "OK" : "NG"); //OK

var_dump($originalObj->child);
var_dump($cloneObj->child);
