class DISample
{
  private $dependency = null;
  public function __construct(Dependency $obj)
  {
    // コンストラクタ渡しのDIパターン
    // 他にセッターで渡しても構わない
    // 依存するオブジェクトを必ず外部注入する
    //new するのが外部になるので依存対象が
    // インタフェースレベルになるから疎結合を実現できる
    $this->dependency = $obj;
  }

  public function getDependency()
  {
    return $this->dependency;
  }
}

