        StringBuffer buffer = new StringBuffer();
        buffer.append("" + title + "\n");
        buffer.append("\n");
        buffer.append("" + title + "\n");
        buffer.append("\n");
        Iterator it = content.iterator();
        while (it.hasNext()) {
            Item item = (Item)it.next();
            buffer.append("" + item.makeHTML() + "");
        }
        buffer.append("\n");
        buffer.append("" + author + "");
        buffer.append("\n");
        return buffer.toString();
    }
}

